from square import *from exceptions import *class Board:        def __init__(self):            "create a new board object"            self._squareArray = [[Square() for _ in range(5)] for _ in range(5)]        def getSquare(self, x, y):            "return the square at pos(x,y) counting from (0,0) in the top left corner"            if x < 0 or x > 4 or y < 0 or y > 4:                raise InvalidSquareError            return self._squareArray[y][x]        def __repr__(self):            s = "+--+--+--+--+--+"            for row in self._squareArray:                s += "\n|"                for sq in row:                    s += repr(sq)                    s += "|"                s += "\n+--+--+--+--+--+"            return s