import squareclass Board:        def __init__(self):            "create a new board object"            self._squareArray = list()            i = 0            j = 0            while i < 5:                row = list()                while j < 5:                    row.append(square.Square())                list.append(row)        def getSquare(self, x, y):            "return the square at pos(x,y) counting from (0,0) in the top left corner"            return self._squareArray[y][x]        def __repr__(self):            s = "+--+--+--+--+--+"            for row in self._squareArray:                s += "|"                for sq in row:                    s += repr(sq)                    s += "|"                s += "+--+--+--+--+--+"            return s