import boardimport copyimport playerclass Game:    def __init__(self, type1, type2):        '''Create a new game object'''        self._board = board.Board()        self._player1 = player.Player(type1)        self._player2 = player.Player(type2)        self._activePlayer = self._player1    def save(self):        '''Save a copy of the current game'''        # more efficient way to do this is to save only the necessary information        # that can be a TODO for later        return copy.deepcopy(self)    def nextTurn(self):        if self._activePlayer == self._player1:            self._activePlayer = self._player2        else:            self._activePlayer = self._player1