from worker import *from board import *from exceptions import *directions = ["n", "ne", "e", "se", "s", "sw", "w", "nw"]class Player:    def __init__(self, playerType, playerName, gameboard):        self._workers = list()        self._board = gameboard        if playerType == "human":            self.doMove = self._humanStrategy        elif playerType == "random":            self.doMove = self._randomStrategy        else:            self.doMove = self._heuristicStrategy        self._playerName = playerName        if playerName == "white":            self._createWorker("A", 1, 3)            self._createWorker("B", 3, 1)        else:            self._createWorker("Y", 1, 1)            self._createWorker("Z", 3, 3)    def __repr__(self):        return f"{self._playerName} ({self._workers[0]}{self._workers[1]})"    def setOpponent(self, opponent):        self._opponent = opponent    def _createWorker(self, name, x, y):        w = Worker(name, self, x, y, self._board)        self._workers.append(w)    def _selectWorker(self, name):        for w in self._workers:            if repr(w) == name:                return w        raise InvalidWorkerError()    def _getAllPossibleMoves(self):        raise NotImplementedError    def _calculateHeuristic(self):        raise NotImplementedError    def _humanStrategy(self):        w = None        validStep = False        while not validStep:            try:                worker = input("Select a worker to move\n")                w = self._selectWorker(worker)                validStep = True            except InvalidWorkerError:                try:                    self._opponent._selectWorker(worker)                    print("That is not your worker")                except InvalidWorkerError:                    print("Not a valid worker")        validStep = False        while not validStep:            try:                moveDirection = input("Select a direction to move (n, ne, e, se, s, sw, w, nw)\n")                validStep = w.checkValidMove(moveDirection)            except InvalidDirectionError:                print("Not a valid direction")            except (InvalidMoveError, InvalidSquareError):                print(f"Cannot move {moveDirection}")        w.move(moveDirection)        validStep = False        while not validStep:            try:                buildDirection = input("Select a direction to build (n, ne, e, se, s, sw, w, nw)\n")                validStep = w.checkValidBuild(buildDirection)            except InvalidDirectionError:                print("Not a valid direction")            except (InvalidBuildError, InvalidSquareError):                print(f"Cannot build {moveDirection}")        w.build(buildDirection)    def _randomStrategy(self):        raise NotImplementedError    def _heuristicStrategy(self):        raise NotImplementedError